import { useEffect, useState } from "react";

function App() {
  const [categories, setCategories] = useState({});
  const [selectedImage, setSelectedImage] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [modelType, setModelType] = useState("cnn");

  useEffect(() => {
    fetch("http://127.0.0.1:5000/categories")
      .then((res) => res.json())
      .then((data) => setCategories(data))
      .catch((err) => console.error("Error fetching categories:", err));
  }, []);

  const handlePredict = () => {
    if (!selectedImage) return;

    fetch("http://127.0.0.1:5000/predict", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ image_url: selectedImage, model_type: modelType }),
    })
      .then((res) => res.json())
      .then((data) => setPrediction(data))
      .catch((err) => console.error("Error predicting:", err));
  };

  return (
    <div>
      <h1>CIFAR-10 Image Classifier</h1>
      
      <label>Select Model:</label>
      <select value={modelType} onChange={(e) => setModelType(e.target.value)}>
        <option value="cnn">CNN</option>
        <option value="resnet">ResNet</option>
        <option value="vgg">VGG</option>
      </select>

      {Object.keys(categories).length === 0 ? (
        <p>Loading images...</p>
      ) : (
        <div>
          {Object.entries(categories).map(([category, images]) => (
            <div key={category}>
              <h2>{category}</h2>
              {images.map((img) => (
                <img
                  key={img}
                  src={`http://127.0.0.1:5000/image/${category}/${img}`}
                  alt={img}
                  width={100}
                  style={{ cursor: "pointer", margin: 5 }}
                  onClick={() =>
                    setSelectedImage(`http://127.0.0.1:5000/image/${category}/${img}`)
                  }
                />
              ))}
            </div>
          ))}
        </div>
      )}

      {selectedImage && (
        <div>
          <h2>Selected Image</h2>
          <img src={selectedImage} alt="Selected" width={200} />
          <button onClick={handlePredict}>Predict</button>
        </div>
      )}

      {prediction && (
        <div>
          <h2>Prediction Result</h2>
          <p>
            Class: <strong>{prediction.class_name}</strong>
          </p>
          <p>
            Confidence: <strong>{(prediction.confidence * 100).toFixed(2)}%</strong>
          </p>
        </div>
      )}
    </div>
  );
}

export default App;
