// src/components/ui/selects.js
export function Select({ value, onChange, children }) {
    return (
      <select className="border p-2 rounded" value={value} onChange={onChange}>
        {children}
      </select>
    );
  }
  
  export function SelectItem({ value, children }) {
    return <option value={value}>{children}</option>;
  }
  
  // üõ† Th√™m c√°c component b·ªã thi·∫øu
  export function SelectTrigger({ children }) {
    return <div className="p-2 border rounded cursor-pointer">{children}</div>;
  }
  
  export function SelectValue({ value }) {
    return <span>{value}</span>;
  }
  
  export function SelectContent({ children }) {
    return <div className="absolute bg-white border rounded shadow-md">{children}</div>;
  }
